import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";

class App extends Component {
  state = {
    monsters: [
      {
        id: 'm0',
        name: 'Frankenstain'
      },
      {
        id: 'm1',
        name: 'Dracula'
      },
      {
        id: 'm2',
        name: 'La llorona' 
      }
    ]
  };
  render() {
    const { monsters } = this.state;
    return (
      <div className="App">
       {
         monsters.map(monster => (
         <h1 key={monster.id}>{monster.name}</h1> 
         ))
       }
      </div>
    );
  }
}

export default App;
